#include <stdint.h>

int main(void)
{
    /* Address of RCC AHB1 peripheral clock enable register (RCC_AHB1ENR):
     *     0x4002 3800 + 0x30 = 0x4002 3830
     *
     * Address of GPIO port mode register (GPIOD_MODER): (mode control)
     *     0x4002 0C00 + 0x00 = 0x4002 0C00
     *
     * Address of GPIO port output data register (GPIOD_ODR): (write data)
     * 	   0x4002 0C00 + 0x14 = 0x4002 0C14
     *
     * Adress of GPIO port input data register (GPIOD_IDR) (read data)
     * 	   0x4002 0C00 + 0x10 = 0x4002 0C10
     */

	// -------------------------------------------------------------------------------------

	uint32_t volatile *const pRCC_AHB1ENR = (uint32_t *)0x40023830;
	uint32_t volatile *const pGPIOD_MODER = (uint32_t *)0x40020C00;
	uint32_t volatile *const pGPIOD_ODR = (uint32_t *)0x40020C14;
	uint32_t const volatile *const pGPIOD_IDR = (uint32_t *)0x40020C10;

	*pRCC_AHB1ENR |= (1 << 3);  // Clock ativado para GPIOD
	*pGPIOD_MODER |= (1 << 24);  // General purpose output mode no pino PD12

	for(;;){


		if (*pGPIOD_IDR & 1)
		*pGPIOD_ODR |= (1 << 12);  // PD12 alto

		else
		*pGPIOD_ODR &= ~(1 << 12);  // PD12 baixo

	}


}
